import React, { useState } from "react";
import { Card, Pagination } from "react-bootstrap";

const dummyArticles = Array.from({ length: 20 }, (_, i) => ({
  title: `Article ${i + 1}`,
  content: "Lorem ipsum dolor sit amet, consectetur adipiscing elit.",
}));

function MyArticles() {
  const [currentPage, setCurrentPage] = useState(1);
  const articlesPerPage = 5;

  const indexOfLast = currentPage * articlesPerPage;
  const indexOfFirst = indexOfLast - articlesPerPage;
  const currentArticles = dummyArticles.slice(indexOfFirst, indexOfLast);

  const totalPages = Math.ceil(dummyArticles.length / articlesPerPage);

  const paginationItems = [];
  for (let number = 1; number <= totalPages; number++) {
    paginationItems.push(
      <Pagination.Item
        key={number}
        active={number === currentPage}
        onClick={() => setCurrentPage(number)}
        style={{
          borderRadius: "50%",
          backgroundColor: number === currentPage ? "#343a40" : "#e0e0e0",
          color: number === currentPage ? "#fff" : "#000",
          margin: "0 5px",
        }}
      >
        {number}
      </Pagination.Item>
    );
  }

  return (
    <div>
      {currentArticles.map((article, idx) => (
        <Card key={idx} style={{ marginBottom: "10px" }}>
          <Card.Body>
            <Card.Title>{article.title}</Card.Title>
            <Card.Text>{article.content}</Card.Text>
          </Card.Body>
        </Card>
      ))}

      <Pagination style={{ display: "flex", justifyContent: "center" }}>
        {paginationItems}
      </Pagination>
    </div>
  );
}

export default MyArticles;
