import React, { useState } from "react";
import { Table, Badge, Pagination } from "react-bootstrap";

// Dummy data
const dummyArticles = Array.from({ length: 23 }, (_, i) => ({
  id: i + 1,
  title: `Article Title ${i + 1}`,
  status: i % 3 === 0 ? "Draft" : "Published",
}));

function MyArticles() {
  const [currentPage, setCurrentPage] = useState(1);
  const articlesPerPage = 5;

  // Pagination logic
  const indexOfLast = currentPage * articlesPerPage;
  const indexOfFirst = indexOfLast - articlesPerPage;
  const currentArticles = dummyArticles.slice(indexOfFirst, indexOfLast);

  const totalPages = Math.ceil(dummyArticles.length / articlesPerPage);

  return (
    <div>
      <h2>My Articles</h2>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>#</th>
            <th>Title</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {currentArticles.map((article) => (
            <tr key={article.id}>
              <td>{article.id}</td>
              <td>{article.title}</td>
              <td>
                <Badge pill bg={article.status === "Published" ? "success" : "warning"}>
                  {article.status}
                </Badge>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Pagination */}
      <Pagination>
        <Pagination.Prev
          onClick={() => setCurrentPage((p) => Math.max(p - 1, 1))}
          disabled={currentPage === 1}
        />
        {[...Array(totalPages)].map((_, i) => (
          <Pagination.Item
            key={i}
            active={i + 1 === currentPage}
            onClick={() => setCurrentPage(i + 1)}
          >
            {i + 1}
          </Pagination.Item>
        ))}
        <Pagination.Next
          onClick={() => setCurrentPage((p) => Math.min(p + 1, totalPages))}
          disabled={currentPage === totalPages}
        />
      </Pagination>
    </div>
  );
}

export default MyArticles;
