import React, { useState } from "react";
import { Table, Badge, Pagination } from "react-bootstrap";

function MyArticles() {
  // Dummy article data
  const articles = Array.from({ length: 23 }, (_, i) => ({
    id: i + 1,
    title: `Article ${i + 1}`,
    status: ["Published", "Draft", "Pending"][i % 3],
  }));

  const [currentPage, setCurrentPage] = useState(1);
  const pageSize = 5;
  const totalPages = Math.ceil(articles.length / pageSize);

  const getBadgeVariant = (status) => {
    switch (status) {
      case "Published":
        return "success";
      case "Draft":
        return "warning";
      case "Pending":
        return "secondary";
      default:
        return "primary";
    }
  };

  const paginatedArticles = articles.slice(
    (currentPage - 1) * pageSize,
    currentPage * pageSize
  );

  const handlePageChange = (page) => setCurrentPage(page);

  // Custom pagination style
  const paginationStyle = {
    backgroundColor: "#343a40", // dark gray background
    borderRadius: "5px",
    padding: "5px 10px",
    display: "inline-flex",
  };

  const pageItemStyle = {
    backgroundColor: "#495057", // slightly lighter gray
    color: "#fff",
    margin: "0 3px",
    border: "none",
    cursor: "pointer",
  };

  const activeStyle = {
    backgroundColor: "#0d6efd", // blue for active page
    color: "#fff",
    border: "none",
  };

  return (
    <>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>#</th>
            <th>Title</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {paginatedArticles.map((article) => (
            <tr key={article.id}>
              <td>{article.id}</td>
              <td>{article.title}</td>
              <td>
                <Badge pill bg={getBadgeVariant(article.status)}>
                  {article.status}
                </Badge>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      <div style={paginationStyle}>
        {Array.from({ length: totalPages }, (_, i) => (
          <div
            key={i + 1}
            onClick={() => handlePageChange(i + 1)}
            style={i + 1 === currentPage ? activeStyle : pageItemStyle}
          >
            {i + 1}
          </div>
        ))}
      </div>
    </>
  );
}

export default MyArticles;
