import React, { useState } from "react";
import { Table, Badge } from "react-bootstrap";

function MyArticles() {
  // 23 random current affair / light news titles
  const articles = [
    { id: 1, title: "Local Farmers Celebrate Harvest Festival", status: "Published" },
    { id: 2, title: "City Park Renovation Completed", status: "Draft" },
    { id: 3, title: "New Community Library Opens", status: "Pending" },
    { id: 4, title: "Tech Startup Launches Eco-Friendly App", status: "Published" },
    { id: 5, title: "Neighborhood Hosts Charity Run", status: "Draft" },
    { id: 6, title: "Café Introduces Seasonal Menu", status: "Published" },
    { id: 7, title: "Local Band Wins Music Contest", status: "Pending" },
    { id: 8, title: "City Council Approves Bike Lanes", status: "Published" },
    { id: 9, title: "Farmers Market Adds Organic Produce Section", status: "Draft" },
    { id: 10, title: "Tech Workshop for Students Announced", status: "Published" },
    { id: 11, title: "New Art Exhibition Opens Downtown", status: "Pending" },
    { id: 12, title: "Library Hosts Storytelling Event", status: "Published" },
    { id: 13, title: "Local School Wins Robotics Competition", status: "Draft" },
    { id: 14, title: "Coffee Festival Brings Visitors Together", status: "Published" },
    { id: 15, title: "Community Garden Expansion Completed", status: "Pending" },
    { id: 16, title: "Startup Launches Sustainable Packaging", status: "Published" },
    { id: 17, title: "Neighborhood Cleanup Drive Successful", status: "Draft" },
    { id: 18, title: "City Theater Premieres New Play", status: "Published" },
    { id: 19, title: "Tech Conference Attracts International Guests", status: "Pending" },
    { id: 20, title: "Local Chef Wins Culinary Award", status: "Published" },
    { id: 21, title: "Charity Bake Sale Raises Funds for School", status: "Draft" },
    { id: 22, title: "New Cycling Trail Opens in City Park", status: "Published" },
    { id: 23, title: "Community Center Offers Free Yoga Classes", status: "Pending" },
  ];

  const [currentPage, setCurrentPage] = useState(1);
  const pageSize = 5;
  const totalPages = Math.ceil(articles.length / pageSize);

  const getBadgeVariant = (status) => {
    switch (status) {
      case "Published": return "success";
      case "Draft": return "warning";
      case "Pending": return "secondary";
      default: return "primary";
    }
  };

  const paginatedArticles = articles.slice(
    (currentPage - 1) * pageSize,
    currentPage * pageSize
  );

  const handlePageChange = (page) => setCurrentPage(page);

  const paginationStyle = { display: "flex", gap: "10px", marginTop: "20px" };
  const pageItemStyle = {
    width: "40px",
    height: "40px",
    borderRadius: "50%",
    backgroundColor: "#d3d3d3",
    color: "#000",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    cursor: "pointer",
    userSelect: "none",
    transition: "all 0.2s",
  };
  const activeStyle = {
    ...pageItemStyle,
    backgroundColor: "#343a40",
    color: "#fff",
    fontWeight: "bold",
  };
  const hoverStyle = { backgroundColor: "#bcbcbc" };

  return (
    <div style={{ marginLeft: "240px", padding: "20px" }}>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>#</th>
            <th>Title</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {paginatedArticles.map((article) => (
            <tr key={article.id}>
              <td>{article.id}</td>
              <td>{article.title}</td>
              <td>
                <Badge pill bg={getBadgeVariant(article.status)}>
                  {article.status}
                </Badge>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      <div style={paginationStyle}>
        {Array.from({ length: totalPages }, (_, i) => {
          const isActive = i + 1 === currentPage;
          return (
            <div
              key={i + 1}
              onClick={() => handlePageChange(i + 1)}
              style={isActive ? activeStyle : pageItemStyle}
              onMouseEnter={(e) => !isActive && (e.target.style.backgroundColor = hoverStyle.backgroundColor)}
              onMouseLeave={(e) => !isActive && (e.target.style.backgroundColor = pageItemStyle.backgroundColor)}
            >
              {i + 1}
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default MyArticles;
