import React, { useState } from "react";
import { Table, Badge } from "react-bootstrap";

function MyArticles() {
  const articles = Array.from({ length: 23 }, (_, i) => ({
    id: i + 1,
    title: `Article ${i + 1}`,
    status: ["Published", "Draft", "Pending"][i % 3],
  }));

  const [currentPage, setCurrentPage] = useState(1);
  const pageSize = 5;
  const totalPages = Math.ceil(articles.length / pageSize);

  const getBadgeVariant = (status) => {
    switch (status) {
      case "Published":
        return "success";
      case "Draft":
        return "warning";
      case "Pending":
        return "secondary";
      default:
        return "primary";
    }
  };

  const paginatedArticles = articles.slice(
    (currentPage - 1) * pageSize,
    currentPage * pageSize
  );

  const handlePageChange = (page) => setCurrentPage(page);

  // Pagination styles
  const paginationStyle = {
    display: "flex",
    gap: "10px",
    marginTop: "20px",
  };

  const pageItemStyle = {
    width: "40px",
    height: "40px",
    borderRadius: "50%", // fully round
    backgroundColor: "#d3d3d3", // light gray for inactive pages
    color: "#000",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    cursor: "pointer",
    userSelect: "none",
    transition: "all 0.2s",
  };

  const activeStyle = {
    ...pageItemStyle,
    backgroundColor: "#343a40", // dark gray for active page
    color: "#fff",
    fontWeight: "bold",
  };

  const hoverStyle = {
    backgroundColor: "#bcbcbc", // slightly darker light gray on hover
  };

  return (
    <>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>#</th>
            <th>Title</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {paginatedArticles.map((article) => (
            <tr key={article.id}>
              <td>{article.id}</td>
              <td>{article.title}</td>
              <td>
                <Badge pill bg={getBadgeVariant(article.status)}>
                  {article.status}
                </Badge>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      <div style={paginationStyle}>
        {Array.from({ length: totalPages }, (_, i) => {
          const isActive = i + 1 === currentPage;
          return (
            <div
              key={i + 1}
              onClick={() => handlePageChange(i + 1)}
              style={isActive ? activeStyle : pageItemStyle}
              onMouseEnter={(e) => {
                if (!isActive) e.target.style.backgroundColor = hoverStyle.backgroundColor;
              }}
              onMouseLeave={(e) => {
                if (!isActive) e.target.style.backgroundColor = pageItemStyle.backgroundColor;
              }}
            >
              {i + 1}
            </div>
          );
        })}
      </div>
    </>
  );
}

export default MyArticles;
