import React from "react";
import { Nav } from "react-bootstrap";
import { FaUser, FaFileAlt, FaCog } from "react-icons/fa"; // icons

function Sidebar({ collapsed, page, setPage }) {
  const handleClick = (e, targetPage) => {
    e.preventDefault();
    setPage(targetPage);
  };

  const navItems = [
    { name: "Profile", icon: <FaUser />, page: "profile" },
    { name: "My Articles", icon: <FaFileAlt />, page: "articles" },
    { name: "Settings", icon: <FaCog />, page: "settings" },
  ];

  return (
    <Nav className="flex-column p-3">
      {navItems.map((item) => (
        <Nav.Link
          key={item.page}
          href="#"
          active={page === item.page}
          onClick={(e) => handleClick(e, item.page)}
          style={{ display: "flex", alignItems: "center", gap: "10px" }}
        >
          {item.icon} {/* show icon always */}
          {!collapsed && item.name} {/* show text only when expanded */}
        </Nav.Link>
      ))}
    </Nav>
  );
}

export default Sidebar;
