import React, { useState } from "react";
import { Button, ListGroup } from "react-bootstrap";
import { List, X } from "react-bootstrap-icons"; // hamburger & close icons

function Sidebar({ setPage }) {
  const [collapsed, setCollapsed] = useState(false);

  const toggleSidebar = () => setCollapsed(!collapsed);

  const sidebarStyle = {
    width: collapsed ? "60px" : "220px",
    transition: "width 0.3s",
    overflow: "hidden",
    background: "#343a40", // dark gray sidebar
    color: "#fff",
    height: "100vh",
    padding: "15px 10px",
    display: "flex",
    flexDirection: "column",
    alignItems: collapsed ? "center" : "flex-start",
  };

  const buttonStyle = {
    marginBottom: "20px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    borderRadius: "50%",
    width: "40px",
    height: "40px",
    padding: 0,
    backgroundColor: "#007bff",
    border: "none",
    color: "#fff",
  };

  const listItemStyle = {
    background: "transparent",
    color: "#fff",
    border: "none",
    cursor: "pointer",
  };

  return (
    <div style={sidebarStyle}>
      {/* Collapse / Expand button with new icon */}
      <Button onClick={toggleSidebar} style={buttonStyle}>
        {collapsed ? <List size={20} /> : <X size={20} />}
      </Button>

      {/* Sidebar links */}
      {!collapsed && (
        <ListGroup style={{ width: "100%" }}>
          <ListGroup.Item
            action
            onClick={() => setPage("profile")}
            style={listItemStyle}
          >
            Profile
          </ListGroup.Item>
          <ListGroup.Item
            action
            onClick={() => setPage("articles")}
            style={listItemStyle}
          >
            My Articles
          </ListGroup.Item>
        </ListGroup>
      )}
    </div>
  );
}

export default Sidebar;
