import React from "react";
import { FaUser, FaNewspaper, FaBars } from "react-icons/fa";
import Image from "react-bootstrap/Image";
import profilePic from "../assets/profile.jpg";

function Sidebar({ activePage, setActivePage, collapsed, setCollapsed }) {
  const sidebarStyle = {
    height: "100vh",
    backgroundColor: "#343a40",
    color: "#fff",
    position: "fixed",
    top: 0,
    left: 0,
    width: collapsed ? "80px" : "240px",
    transition: "width 0.3s",
    paddingTop: "20px",
    overflow: "hidden",
  };

  const profileStyle = {
    display: "flex",
    alignItems: "center",
    justifyContent: collapsed ? "center" : "flex-start",
    padding: "0 10px",
    marginBottom: "20px",
  };

  const linkStyle = (page) => ({
    padding: "12px 20px",
    cursor: "pointer",
    backgroundColor: activePage === page ? "#495057" : "transparent",
    margin: "8px 10px",
    borderRadius: "8px",
    border: activePage === page ? "2px solid #fff" : "2px solid transparent",
    display: "flex",
    alignItems: "center",
  });

  return (
    <div style={sidebarStyle}>
      {/* Profile */}
      <div style={profileStyle}>
        <Image
          src={profilePic}
          roundedCircle
          width={collapsed ? 40 : 50}
          height={collapsed ? 40 : 50}
        />
        {!collapsed && (
          <span style={{ marginLeft: "10px", fontWeight: "bold" }}>
            Mirazum Munira
          </span>
        )}
      </div>

      {/* Collapse button */}
      <div
        onClick={() => setCollapsed(!collapsed)}
        style={{
          display: "flex",
          justifyContent: collapsed ? "center" : "flex-end",
          padding: "0 10px",
          marginBottom: "20px",
          cursor: "pointer",
        }}
      >
        <FaBars />
      </div>

      {/* Links */}
      <div style={linkStyle("profile")} onClick={() => setActivePage("profile")}>
        <FaUser style={{ marginRight: collapsed ? 0 : "10px" }} />
        {!collapsed && "Profile"}
      </div>
      <div
        style={linkStyle("articles")}
        onClick={() => setActivePage("articles")}
      >
        <FaNewspaper style={{ marginRight: collapsed ? 0 : "10px" }} />
        {!collapsed && "My Articles"}
      </div>
    </div>
  );
}

export default Sidebar;
