import React from "react";
import { Button, ListGroup, Image } from "react-bootstrap";
import { List, X } from "react-bootstrap-icons";
import profilePic from "../assets/profile.jpg";

function Sidebar({ collapsed, setCollapsed, setPage }) {
  // toggle function uses prop from App
  const toggleSidebar = () => {
    setCollapsed(!collapsed);
  };

  const sidebarStyle = {
    width: collapsed ? "60px" : "220px",
    transition: "width 0.3s",
    overflow: "hidden",
    background: "#343a40",
    color: "#fff",
    height: "100vh",
    padding: "15px 10px",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    position: "fixed",
    top: 0,
    left: 0,
    zIndex: 1000,
  };

  const profileStyle = {
    display: "flex",
    alignItems: "center",
    marginBottom: "20px",
    width: "100%",
    justifyContent: collapsed ? "center" : "flex-start",
  };

  const buttonStyle = {
    marginBottom: "20px",
    borderRadius: "50%",
    width: "40px",
    height: "40px",
    padding: 0,
    backgroundColor: "transparent",
    border: "none",
    color: "#fff",
    cursor: "pointer",
    outline: "none",
  };

  const listItemStyle = {
    background: "transparent",
    color: "#fff",
    border: "none",
    cursor: "pointer",
    textAlign: collapsed ? "center" : "left",
    paddingLeft: collapsed ? 0 : "10px",
  };

  return (
    <div style={sidebarStyle}>
      {/* Profile */}
      <div style={profileStyle}>
        <Image
          src={profilePic}
          roundedCircle
          width={collapsed ? 30 : 40}
          height={collapsed ? 30 : 40}
        />
        {!collapsed && (
          <span style={{ marginLeft: "10px", fontWeight: "bold" }}>John Doe</span>
        )}
      </div>

      {/* Collapse button */}
      <Button onClick={toggleSidebar} style={buttonStyle}>
        {collapsed ? <List size={20} /> : <X size={20} />}
      </Button>

      {/* Links */}
      <ListGroup style={{ width: "100%", background: "transparent" }}>
        <ListGroup.Item
          action
          onClick={() => setPage("profile")}
          style={listItemStyle}
        >
          {!collapsed ? "Profile" : "P"}
        </ListGroup.Item>
        <ListGroup.Item
          action
          onClick={() => setPage("articles")}
          style={listItemStyle}
        >
          {!collapsed ? "My Articles" : "A"}
        </ListGroup.Item>
      </ListGroup>
    </div>
  );
}

export default Sidebar;
