import React, { useState } from "react";
import { Button, ListGroup, Image } from "react-bootstrap";
import { List, X } from "react-bootstrap-icons";

function Sidebar({ setPage }) {
  const [collapsed, setCollapsed] = useState(false);

  const toggleSidebar = () => setCollapsed(!collapsed);

  const user = {
    fullName: "John Doe",
    profilePic: "https://via.placeholder.com/40",
  };

  const sidebarStyle = {
    width: collapsed ? "60px" : "220px",
    transition: "width 0.3s",
    overflow: "hidden",
    background: "#343a40", // dark gray
    color: "#fff",
    height: "100vh",
    padding: "15px 10px",
    display: "flex",
    flexDirection: "column",
    alignItems: collapsed ? "center" : "flex-start",
    position: "relative",
  };

  const buttonStyle = {
    marginBottom: "20px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    borderRadius: "50%",
    width: "40px",
    height: "40px",
    padding: 0,
    backgroundColor: "#fff", // button stays white for contrast
    border: "none",
    color: "#343a40", // icon color matches dark gray theme
  };

  const listItemStyle = {
    background: "transparent",
    color: "#fff",
    border: "none",
    cursor: "pointer",
  };

  const profileStyle = {
    display: "flex",
    alignItems: "center",
    marginBottom: "20px",
    width: "100%",
  };

  const nameStyle = {
    marginLeft: collapsed ? "0" : "10px",
    fontWeight: "bold",
    fontSize: "0.9rem",
    whiteSpace: "nowrap",
  };

  return (
    <div style={sidebarStyle}>
      {/* Profile at top */}
      <div style={profileStyle}>
        <Image
          src={user.profilePic}
          roundedCircle
          width={collapsed ? 30 : 40}
          height={collapsed ? 30 : 40}
        />
        {!collapsed && <span style={nameStyle}>{user.fullName}</span>}
      </div>

      {/* Collapse / Expand button */}
      <Button onClick={toggleSidebar} style={buttonStyle}>
        {collapsed ? <List size={20} /> : <X size={20} />}
      </Button>

      {/* Sidebar links */}
      {!collapsed && (
        <ListGroup style={{ width: "100%" }}>
          <ListGroup.Item
            action
            onClick={() => setPage("profile")}
            style={listItemStyle}
          >
            Profile
          </ListGroup.Item>
          <ListGroup.Item
            action
            onClick={() => setPage("articles")}
            style={listItemStyle}
          >
            My Articles
          </ListGroup.Item>
        </ListGroup>
      )}
    </div>
  );
}

export default Sidebar;
