import React, { useState } from "react";
import { Button, ListGroup } from "react-bootstrap";
import { ChevronLeft, ChevronRight } from "react-bootstrap-icons"; // make sure to install react-bootstrap-icons

function Sidebar({ setPage }) {
  const [collapsed, setCollapsed] = useState(false);

  const toggleSidebar = () => setCollapsed(!collapsed);

  const sidebarStyle = {
    width: collapsed ? "60px" : "200px",
    transition: "width 0.3s",
    overflow: "hidden",
    background: "#f8f9fa",
    height: "100vh",
    padding: "10px",
    display: "flex",
    flexDirection: "column",
    alignItems: collapsed ? "center" : "flex-start",
  };

  return (
    <div style={sidebarStyle}>
      {/* Styled Collapse / Expand button */}
      <Button
        variant="outline-primary"
        size="sm"
        onClick={toggleSidebar}
        style={{
          marginBottom: "15px",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          borderRadius: "50%",
          width: "35px",
          height: "35px",
          padding: 0,
        }}
      >
        {collapsed ? <ChevronRight /> : <ChevronLeft />}
      </Button>

      {/* Sidebar links */}
      {!collapsed && (
        <ListGroup style={{ width: "100%" }}>
          <ListGroup.Item action onClick={() => setPage("profile")}>
            Profile
          </ListGroup.Item>
          <ListGroup.Item action onClick={() => setPage("articles")}>
            My Articles
          </ListGroup.Item>
        </ListGroup>
      )}
    </div>
  );
}

export default Sidebar;
