import React, { useState } from "react";
import { Button, ListGroup, Image } from "react-bootstrap";
import { List, X } from "react-bootstrap-icons";
import profilePic from "../assets/profile.jpg";

function Sidebar({ setPage, page }) {
  const [collapsed, setCollapsed] = useState(false);

  const toggleSidebar = () => setCollapsed(!collapsed);

  const user = { fullName: "Mirazum Munira" };

  const sidebarStyle = {
    width: collapsed ? "60px" : "220px",
    transition: "width 0.3s",
    overflow: "hidden",
    background: "#343a40",
    color: "#fff",
    height: "100vh",
    padding: "15px 10px",
    display: "flex",
    flexDirection: "column",
    alignItems: collapsed ? "center" : "flex-start",
    position: "fixed",
  };

  const buttonStyle = {
    marginBottom: "20px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    borderRadius: "50%",
    width: "40px",
    height: "40px",
    padding: 0,
    backgroundColor: "#fff",
    border: "none",
    color: "#343a40",
    cursor: "pointer",
  };

  const listItemStyle = {
    background: "transparent",
    color: "#fff",
    border: "none",
    cursor: "pointer",
    padding: "10px 15px",
    borderRadius: "5px",
    marginBottom: "5px",
    transition: "all 0.2s",
  };

  const activeStyle = {
    ...listItemStyle,
    border: "2px solid #fff",
    borderRadius: "5px",
  };

  const profileStyle = {
    display: "flex",
    alignItems: "center",
    marginBottom: "20px",
    width: "100%",
  };

  const nameStyle = {
    marginLeft: collapsed ? "0" : "10px",
    fontWeight: "bold",
    fontSize: "0.9rem",
    whiteSpace: "nowrap",
  };

  const getLinkStyle = (linkPage) => {
    if (collapsed) return {};
    return linkPage === page ? activeStyle : listItemStyle;
  };

  return (
    <div style={sidebarStyle}>
      {/* Profile */}
      <div style={profileStyle}>
        <Image
          src={profilePic}
          roundedCircle
          width={collapsed ? 30 : 40}
          height={collapsed ? 30 : 40}
        />
        {!collapsed && <span style={nameStyle}>{user.fullName}</span>}
      </div>

      {/* Collapse button */}
      <Button onClick={toggleSidebar} style={buttonStyle}>
        {collapsed ? <List size={20} /> : <X size={20} />}
      </Button>

      {/* Links */}
      {!collapsed && (
        <ListGroup style={{ width: "100%" }}>
          <ListGroup.Item
            action
            onClick={() => setPage("profile")}
            style={getLinkStyle("profile")}
          >
            Profile
          </ListGroup.Item>
          <ListGroup.Item
            action
            onClick={() => setPage("articles")}
            style={getLinkStyle("articles")}
          >
            My Articles
          </ListGroup.Item>
        </ListGroup>
      )}
    </div>
  );
}

export default Sidebar;
