import React, { useState } from "react";
import { Table, Badge, Pagination } from "react-bootstrap";

// Dummy current affair topics
const topics = [
  "Global Climate Summit Updates",
  "New AI Regulations Introduced",
  "Stock Market Hits Record High",
  "SpaceX Launches New Mission",
  "World Health Organization Reports",
  "New Education Policy Announced",
  "International Trade Agreements Signed",
  "Breakthrough in Renewable Energy",
  "Global Sports Championship Highlights",
  "Tech Giants Release Quarterly Results",
  "Political Elections Around the World",
  "Economic Forecast for Next Year",
  "Major Cybersecurity Breach Reported",
  "New Scientific Discovery in Medicine",
  "Environmental Conservation Initiatives",
  "International Peace Talks",
  "Advancements in Quantum Computing",
  "Global Pandemic Updates",
  "New Transportation Infrastructure",
  "Cultural Festivals Around the World",
  "Space Tourism Developments",
  "Human Rights Campaigns",
  "Global Inflation and Prices",
];

// Generate dummy articles
const dummyArticles = topics.map((title, i) => ({
  id: i + 1,
  title,
  status: i % 3 === 0 ? "Draft" : "Published",
}));

function MyArticles() {
  const [currentPage, setCurrentPage] = useState(1);
  const articlesPerPage = 5;

  const indexOfLast = currentPage * articlesPerPage;
  const indexOfFirst = indexOfLast - articlesPerPage;
  const currentArticles = dummyArticles.slice(indexOfFirst, indexOfLast);

  const totalPages = Math.ceil(dummyArticles.length / articlesPerPage);

  return (
    <div>
      <h2>My Articles</h2>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>#</th>
            <th>Title</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {currentArticles.map((article) => (
            <tr key={article.id}>
              <td>{article.id}</td>
              <td>{article.title}</td>
              <td>
                <Badge
                  pill
                  bg={article.status === "Published" ? "success" : "warning"}
                >
                  {article.status}
                </Badge>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Pagination */}
      <Pagination>
        <Pagination.Prev
          onClick={() => setCurrentPage((p) => Math.max(p - 1, 1))}
          disabled={currentPage === 1}
        />
        {[...Array(totalPages)].map((_, i) => (
          <Pagination.Item
            key={i}
            active={i + 1 === currentPage}
            onClick={() => setCurrentPage(i + 1)}
          >
            {i + 1}
          </Pagination.Item>
        ))}
        <Pagination.Next
          onClick={() => setCurrentPage((p) => Math.min(p + 1, totalPages))}
          disabled={currentPage === totalPages}
        />
      </Pagination>
    </div>
  );
}

export default MyArticles;
