import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button, Container, Row, Col } from 'react-bootstrap';
import Sidebar from "./components/Sidebar";
import Profile from "./pages/Profile";
import MyArticles from "./pages/MyArticles";

function App() {
  const [page, setPage] = useState("profile");

  return (
    <Container fluid>
      <Row>
        <Col md={3} style={{ padding: 0 }}>
          <Sidebar />
        </Col>
        <Col md={9} style={{ padding: "20px" }}>
          <div className="mb-3">
            <Button variant="primary" className="me-2" onClick={() => setPage("profile")}>Profile</Button>
            <Button variant="secondary" onClick={() => setPage("articles")}>My Articles</Button>
          </div>
          <hr />
          {page === "profile" ? <Profile /> : <MyArticles />}
        </Col>
      </Row>
    </Container>
  );
}

export default App;
